sys_script.script:
(function executeRule(current, previous /*, g_scratchpad*/) {
    var userSysID = current.user.sys_id;
    var serviceDeskGroupSysID = 'GROUP_SYS_ID_HERE';

    var isMemberOfServiceDeskGroup = gs.getUser().isMemberOf(serviceDeskGroupSysID);

    current.update();
    g_scratchpad.isMemberOfServiceDeskGroup = isMemberOfServiceDeskGroup;
})(current, previous, g_scratchpad);
